import React, {useState} from 'react';

const MovieFinder = props => {

    const [searchText, setSearchText] = useState('food')
    const [movies, setMovies] = useState('Bob')
    const apiKey = "71ea7290"
    function handleSubmit(e) {
        e.preventDefault()
        const search = searchText
        fetch(`http://www.omdbapi.com/?s=${search}&apikey=${apiKey}`)
        .then(res => res.json())
        .then(response => {
            console.log(response)
        })
      .catch(err => {
        console.error(err)
      })
        setSearchText('')
        setMovies(newMovies)
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>Movie Name</label>
                <input value={searchText} onChange={e=> setSearchText(e.target.value)} placeholder='Movie Name' />
                <button type="submit">Search</button>
            </form>
            <p>{movies}</p>

        </div>
    )
}

export default MovieFinder;
